
public class Variable01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int days = 10; // 변수 days를 값 10으로 초기화
		System.out.println(days + " - days"); // days 변수 출력
		
		int speed; // 변수 speed 선언
		speed = 20; // 변수 speed에 값을 20을 저장
		System.out.println(speed + " - speed"); // speed 변수 출력
		
		
	}

}
