
public class Operator04 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10, b = 20;
		int c;
		System.out.println(c = a);
		System.out.println(b += a);
		System.out.println(b -= a);
		System.out.println(b *= a);
		System.out.println(b /= a);
		System.out.println(b %= a);
			
	}

}
